const burger=document?.querySelector(".header__burger"),nav=document?.querySelector(".header__nav"),overlay=document?.querySelector(".overlay"),body=document?.body;function disableScroll(){body.classList.contains("dis-scroll")?body.classList.remove("dis-scroll"):body.classList.add("dis-scroll")}burger.addEventListener("click",()=>{disableScroll(),burger?.classList.toggle("header__burger--active"),nav?.classList.toggle("header__nav--visible"),overlay?.classList.toggle("overlay--visible")});